mapper.select:
SELECT pid, price
FROM product
WHERE pid = #{pid}

SELECT p.pid, p.pname, p.price, p.company, a.uuid, a.uploadpath, a.filename FROM ( SELECT rownum rn, pi.pid, pi.pname, pi.price, pi.company FROM ( SELECT pu.pid, pu.pname, pu.price, pu.productdesc, pu.company, pu.catnum FROM product pu ORDER BY pu.pid DESC ) pi LEFT OUTER JOIN category c ON pi.catnum = c.catnum LEFT OUTER JOIN category cri ON cri.catnum = #{cat} WHERE (c.lft BETWEEN cri.lft AND cri.rgt) AND (c.rgt BETWEEN cri.lft AND cri.rgt) AND
OR pi.pname like '%'||#{keywordValue}||'%'
OR pi.productdesc like '%'||#{keywordValue}||'%'
OR pi.company like '%'||#{keywordValue}||'%'
rownum <= #{page} * #{show}
) p INNER JOIN product_attach a ON p.pid = a.pid AND a.mainimage = 'T' WHERE p.rn > (#{page} -1) * #{show} ORDER BY p.pid DESC
